-- Window snapping functions
local function snapLeft()
  local win = hs.window.focusedWindow()
  if win then win:moveToUnit({0, 0, 0.5, 1}) end
end

local function snapRight()
  local win = hs.window.focusedWindow()
  if win then win:moveToUnit({0.5, 0, 0.5, 1}) end
end

local function maximize()
  local win = hs.window.focusedWindow()
  if win then win:moveToUnit({0, 0, 1, 1}) end
end

local function centerWindow()
  local win = hs.window.focusedWindow()
  if win then win:moveToUnit({0.1, 0.1, 0.8, 0.8}) end
end

-- Window snapping keybindings
hotkey.bind(hyper, "left", snapLeft)
hotkey.bind(hyper, "right", snapRight)
hotkey.bind(hyper, "up", maximize)
hotkey.bind(hyper, "down", centerWindow)

local function switchToNextWindow()
  local app = hs.application.frontmostApplication()
  if not app then return end
  local wins = app:allWindows()
  -- Filter to standard, visible windows
  local visible = hs.fnutils.filter(wins, function(w)
    return w:isStandard() and not w:isMinimized()
  end)
  if #visible < 2 then return end
  -- Second window in the list is the next most recent
  visible[2]:focus()
end

local function switchToStalestWindow()
  local app = hs.application.frontmostApplication()
  if not app then return end
  local wins = app:allWindows()
  local visible = hs.fnutils.filter(wins, function(w)
    return w:isStandard() and not w:isMinimized()
  end)
  if #visible < 2 then return end
  -- Last window in the list is the least recently focused
  visible[#visible]:focus()
end

hotkey.bind(hyper, "w", switchToNextWindow)
hotkey.bind(magic, "w", switchToStalestWindow)

local function getScreenSignature()
  local screens = hs.screen.allScreens()
  local sig = {}
  for _, screen in ipairs(screens) do
    local mode = screen:currentMode()
    table.insert(sig, screen:id() .. ":" .. mode.w .. "x" .. mode.h)
  end
  table.sort(sig)
  return table.concat(sig, "|")
end

local lastScreenSignature = getScreenSignature()

local function handleScreenChange()
  local newSig = getScreenSignature()
  if newSig ~= lastScreenSignature then
    lastScreenSignature = newSig
    hs.reload()
  end
end

screenWatcher = hs.screen.watcher.new(handleScreenChange)
screenWatcher:start()

